  };

  return resumeData.skills
    .filter((skill) => skill.level && skill.level !== "")
    .slice(0, 12) // Show top 12
    .map((skill) => ({
      name: skill.name,
      level: skill.level,
      icon:
        techIcons[skill.name] ||
        `<svg viewBox="0 0 24 24" fill="currentColor" class="w-5 h-5"><path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" fill="#6366F1"/></svg>`,
    }));
});

// Recent companies (last 6), avoiding repeated company names
const recentCompanies = computed(() => {
  const seen = new Set<string>();
  const uniqueJobs = resumeData.work.filter((job) => {
    if (seen.has(job.company)) return false;
    seen.add(job.company);
    return true;
  });
  return uniqueJobs.slice(0, 6).map((job) => ({
    name: job.company,
    role: job.position.split(" ")[0], // First word of position
    year: new Date(job.startDate).getFullYear().toString(),
  }));
});
